function.algorithm:
LOCAL
surf : ifcsurface;
  END_LOCAL;
    surf := arg\ifcpcurve.basissurface;
    RETURN(surf);

LOCAL
u : LIST [2 : 3] OF ifcdirection;
factor : REAL;
d1 : ifcdirection;
d2 : ifcdirection;
  END_LOCAL;
    IF (dim = 3)
THEN
  d1 := NVL(ifcnormalise(axis3), ifcrepresentationitem() || ifcgeometricrepresentationitem() ||
                                 ifcdirection([0.0,0.0,1.0]));
  d2 := ifcfirstprojaxis(d1, axis1);
  u := [d2,ifcsecondprojaxis(d1, d2, axis2),d1];
ELSE
  IF EXISTS(axis1)
  THEN
    d1 := ifcnormalise(axis1);
    u := [d1,ifcorthogonalcomplement(d1)];
    IF EXISTS(axis2)
    THEN
      factor := ifcdotproduct(axis2, u[2]);
      IF (factor < 0.0)
      THEN
        u[2].directionratios[1] := -u[2].directionratios[1];
        u[2].directionratios[2] := -u[2].directionratios[2];
      END_IF;
    END_IF;
  ELSE
    IF EXISTS(axis2)
    THEN
      d1 := ifcnormalise(axis2);
      u := [ifcorthogonalcomplement(d1),d1];
      u[1].directionratios[1] := -u[1].directionratios[1];
      u[1].directionratios[2] := -u[1].directionratios[2];
    ELSE
      u := [ifcrepresentationitem() || ifcgeometricrepresentationitem() || ifcdirection([1.0,0.0]),
            ifcrepresentationitem() || ifcgeometricrepresentationitem() || ifcdirection([0.0,1.0])];
    END_IF;
  END_IF;
END_IF;
    RETURN(u);

  IF b
THEN
  RETURN(choice1);
ELSE
  RETURN(choice2);
END_IF;

LOCAL
d : ifcdirection := NVL(ifcnormalise(refdirection), ifcrepresentationitem() || ifcgeometricrepresentationitem() ||
                                                    ifcdirection([1.0,0.0]));
  END_LOCAL;
    RETURN([d,ifcorthogonalcomplement(d)]);

LOCAL
d1 : ifcdirection;
d2 : ifcdirection;
  END_LOCAL;
    d1 := NVL(ifcnormalise(axis), ifcrepresentationitem() || ifcgeometricrepresentationitem() ||
                                  ifcdirection([0.0,0.0,1.0]));
    d2 := ifcfirstprojaxis(d1, refdirection);
    RETURN([d2,ifcnormalise(ifccrossproduct(d1, d2))\ifcvector.orientation,d1]);

LOCAL
result : BOOLEAN := TRUE;
  END_LOCAL;
    REPEAT i := 1 TO (HIINDEX(segments) - 1);
  IF segments[i][HIINDEX(segments[i])] <> segments[i + 1][1]
  THEN
    BEGIN  
           result := FALSE;
           ESCAPE;
    END;
  END_IF;
END_REPEAT;
    RETURN(result);

LOCAL
result : BOOLEAN := TRUE;
k : INTEGER;
sum : INTEGER;
  END_LOCAL;
    (*Find sum of knot multiplicities.*)

    sum := knotmult[1];
    REPEAT i := 2 TO upknots;
  sum := sum + knotmult[i];
END_REPEAT;
    (*Check limits holding for all B-spline parametrisations*)

    IF (degree < 1) OR (upknots < 2) OR (upcp < degree) OR (sum <> (degree + upcp + 2))
THEN
  result := FALSE;
  RETURN(result);
END_IF;
    k := knotmult[1];
    IF (k < 1) OR (k > degree + 1)
THEN
  result := FALSE;
  RETURN(result);
END_IF;
    REPEAT i := 2 TO upknots;
  IF (knotmult[i] < 1) OR (knots[i] <= knots[i - 1])
  THEN
    result := FALSE;
    RETURN(result);
  END_IF;
  k := knotmult[i];
  IF (i < upknots) AND (k > degree)
  THEN
    result := FALSE;
    RETURN(result);
  END_IF;
  IF (i = upknots) AND (k > degree + 1)
  THEN
    result := FALSE;
    RETURN(result);
  END_IF;
END_REPEAT;
    RETURN(result);

LOCAL
direction2d : ifcdirection := ifcrepresentationitem() || ifcgeometricrepresentationitem() || ifcdirection([0.,1.]);
  END_LOCAL;
    direction2d.directionratios[1] := direction.directionratios[1];
    direction2d.directionratios[2] := direction.directionratios[2];
    RETURN(direction2d);

  CASE m OF
  lengthunit : IF dim = (ifcdimensionalexponents(1, 0, 0, 0, 0, 0, 0))
  THEN
    RETURN(TRUE);
  ELSE
    RETURN(FALSE);
  END_IF;
  massunit : IF dim = (ifcdimensionalexponents(0, 1, 0, 0, 0, 0, 0))
  THEN
    RETURN(TRUE);
  ELSE
    RETURN(FALSE);
  END_IF;
  timeunit : IF dim = (ifcdimensionalexponents(0, 0, 1, 0, 0, 0, 0))
  THEN
    RETURN(TRUE);
  ELSE
    RETURN(FALSE);
  END_IF;
  electriccurrentunit : IF dim = (ifcdimensionalexponents(0, 0, 0, 1, 0, 0, 0))
  THEN
    RETURN(TRUE);
  ELSE
    RETURN(FALSE);
  END_IF;
  thermodynamictemperatureunit : IF dim = (ifcdimensionalexponents(0, 0, 0, 0, 1, 0, 0))
  THEN
    RETURN(TRUE);
  ELSE
    RETURN(FALSE);
  END_IF;
  amountofsubstanceunit : IF dim = (ifcdimensionalexponents(0, 0, 0, 0, 0, 1, 0))
  THEN
    RETURN(TRUE);
  ELSE
    RETURN(FALSE);
  END_IF;
  luminousintensityunit : IF dim = (ifcdimensionalexponents(0, 0, 0, 0, 0, 0, 1))
  THEN
    RETURN(TRUE);
  ELSE
    RETURN(FALSE);
  END_IF;
  planeangleunit : IF dim = (ifcdimensionalexponents(0, 0, 0, 0, 0, 0, 0))
  THEN
    RETURN(TRUE);
  ELSE
    RETURN(FALSE);
  END_IF;
  solidangleunit : IF dim = (ifcdimensionalexponents(0, 0, 0, 0, 0, 0, 0))
  THEN
    RETURN(TRUE);
  ELSE
    RETURN(FALSE);
  END_IF;
  areaunit : IF dim = (ifcdimensionalexponents(2, 0, 0, 0, 0, 0, 0))
  THEN
    RETURN(TRUE);
  ELSE
    RETURN(FALSE);
  END_IF;
  volumeunit : IF dim = (ifcdimensionalexponents(3, 0, 0, 0, 0, 0, 0))
  THEN
    RETURN(TRUE);
  ELSE
    RETURN(FALSE);
  END_IF;
  absorbeddoseunit : IF dim = (ifcdimensionalexponents(2, 0, -2, 0, 0, 0, 0))
  THEN
    RETURN(TRUE);
