    RETURN(FALSE);
  END_IF;
  electricchargeunit : IF dim = (ifcdimensionalexponents(0, 0, 1, 1, 0, 0, 0))
  THEN
    RETURN(TRUE);
  ELSE
    RETURN(FALSE);
  END_IF;
  electricconductanceunit : IF dim = (ifcdimensionalexponents(-2, -1, 3, 2, 0, 0, 0))
  THEN
    RETURN(TRUE);
  ELSE
    RETURN(FALSE);
  END_IF;
  electricvoltageunit : IF dim = (ifcdimensionalexponents(2, 1, -3, -1, 0, 0, 0))
  THEN
    RETURN(TRUE);
