  forceunit : IF dim = (ifcdimensionalexponents(1, 1, -2, 0, 0, 0, 0))
  THEN
    RETURN(TRUE);
  ELSE
    RETURN(FALSE);
  END_IF;
  frequencyunit : IF dim = (ifcdimensionalexponents(0, 0, -1, 0, 0, 0, 0))
  THEN
    RETURN(TRUE);
  ELSE
    RETURN(FALSE);
  END_IF;
  inductanceunit : IF dim = (ifcdimensionalexponents(2, 1, -2, -2, 0, 0, 0))
  THEN
    RETURN(TRUE);
