LOCAL
hatching : INTEGER := 0;
tiles : INTEGER := 0;
colour : INTEGER := 0;
external : INTEGER := 0;
  END_LOCAL;
    external := SIZEOF(QUERY(style <* styles | 'IFC4X2.IFCEXTERNALLYDEFINEDHATCHSTYLE' IN TYPEOF(style)));
    hatching := SIZEOF(QUERY(style <* styles | 'IFC4X2.IFCFILLAREASTYLEHATCHING' IN TYPEOF(style)));
    tiles := SIZEOF(QUERY(style <* styles | 'IFC4X2.IFCFILLAREASTYLETILES' IN TYPEOF(style)));
    colour := SIZEOF(QUERY(style <* styles | 'IFC4X2.IFCCOLOUR' IN TYPEOF(style)));
    IF (external > 1)
THEN
  RETURN(FALSE);
END_IF;
    IF ((external = 1) AND ((hatching > 0) OR (tiles > 0) OR (colour > 0)))
THEN
  RETURN(FALSE);
END_IF;
    IF (colour > 1)
THEN
  RETURN(FALSE);
END_IF;
    IF ((hatching > 0) AND (tiles > 0))
THEN
  RETURN(FALSE);
END_IF;
    RETURN(TRUE);

  IF (EXISTS(relplacement))
THEN
  IF ('IFC4X2.IFCGRIDPLACEMENT' IN TYPEOF(relplacement))
  THEN
    RETURN(?);
  END_IF;
  IF ('IFC4X2.IFCLOCALPLACEMENT' IN TYPEOF(relplacement))
  THEN
    IF ('IFC4X2.IFCAXIS2PLACEMENT2D' IN TYPEOF(axisplacement))
    THEN
      RETURN(TRUE);
    END_IF;
    IF ('IFC4X2.IFCAXIS2PLACEMENT3D' IN TYPEOF(axisplacement))
    THEN
      IF (relplacement\ifclocalplacement.relativeplacement.dim = 3)
      THEN
        RETURN(TRUE);
      ELSE
        RETURN(FALSE);
      END_IF;
    END_IF;
  END_IF;
ELSE
  RETURN(TRUE);
END_IF;
    RETURN(?);

LOCAL
count : INTEGER := 0;
  END_LOCAL;
    IF NOT (EXISTS(constraint))
THEN
  RETURN(TRUE);
END_IF;
    CASE constraint OF
  ifcobjecttypeenum.notdefined : RETURN(TRUE);
  ifcobjecttypeenum.product : BEGIN  
                                     count := SIZEOF(QUERY(temp
                                                           <* objects
                                                           | NOT ('IFC4X2.IFCPRODUCT' IN TYPEOF(temp))));
                                     RETURN(count = 0);
  END;
  ifcobjecttypeenum.process : BEGIN  
                                     count := SIZEOF(QUERY(temp
                                                           <* objects
                                                           | NOT ('IFC4X2.IFCPROCESS' IN TYPEOF(temp))));
                                     RETURN(count = 0);
  END;
  ifcobjecttypeenum.control : BEGIN  
                                     count := SIZEOF(QUERY(temp
                                                           <* objects
                                                           | NOT ('IFC4X2.IFCCONTROL' IN TYPEOF(temp))));
                                     RETURN(count = 0);
  END;
  ifcobjecttypeenum.resource : BEGIN  
                                      count := SIZEOF(QUERY(temp
                                                            <* objects
                                                            | NOT ('IFC4X2.IFCRESOURCE' IN TYPEOF(temp))));
                                      RETURN(count = 0);
  END;
  ifcobjecttypeenum.actor : BEGIN  
                                   count := SIZEOF(QUERY(temp
                                                         <* objects
                                                         | NOT ('IFC4X2.IFCACTOR' IN TYPEOF(temp))));
                                   RETURN(count = 0);
  END;
  ifcobjecttypeenum.group : BEGIN  
                                   count := SIZEOF(QUERY(temp
                                                         <* objects
                                                         | NOT ('IFC4X2.IFCGROUP' IN TYPEOF(temp))));
                                   RETURN(count = 0);
  END;
  ifcobjecttypeenum.project : BEGIN  
                                     count := SIZEOF(QUERY(temp
                                                           <* objects
                                                           | NOT ('IFC4X2.IFCPROJECT' IN TYPEOF(temp))));
                                     RETURN(count = 0);
  END;
  OTHERWISE: RETURN(?);
END_CASE;

LOCAL
namedunitnumber : INTEGER := 0;
derivedunitnumber : INTEGER := 0;
monetaryunitnumber : INTEGER := 0;
namedunitnames : SET OF ifcunitenum := [];
derivedunitnames : SET OF ifcderivedunitenum := [];
  END_LOCAL;
    namedunitnumber := SIZEOF(QUERY(temp
                                    <* units
                                    | ('IFC4X2.IFCNAMEDUNIT' IN TYPEOF(temp)) AND
                                      NOT (temp\ifcnamedunit.unittype = ifcunitenum.userdefined)));
    derivedunitnumber := SIZEOF(QUERY(temp
                                      <* units
                                      | ('IFC4X2.IFCDERIVEDUNIT' IN TYPEOF(temp)) AND
                                        NOT (temp\ifcderivedunit.unittype = ifcderivedunitenum.userdefined)));
    monetaryunitnumber := SIZEOF(QUERY(temp <* units | 'IFC4X2.IFCMONETARYUNIT' IN TYPEOF(temp)));
    REPEAT i := 1 TO SIZEOF(units);
  IF (('IFC4X2.IFCNAMEDUNIT' IN TYPEOF(units[i])) AND NOT (units[i]\ifcnamedunit.unittype = ifcunitenum.userdefined))
  THEN
    namedunitnames := namedunitnames + units[i]\ifcnamedunit.unittype;
  END_IF;
  IF (('IFC4X2.IFCDERIVEDUNIT' IN TYPEOF(units[i])) AND
      NOT (units[i]\ifcderivedunit.unittype = ifcderivedunitenum.userdefined))
  THEN
    derivedunitnames := derivedunitnames + units[i]\ifcderivedunit.unittype;
  END_IF;
END_REPEAT;
    RETURN((SIZEOF(namedunitnames) = namedunitnumber) AND (SIZEOF(derivedunitnames) = derivedunitnumber) AND
           (monetaryunitnumber <= 1));

LOCAL
mag : REAL;
res : ifcdirection;
v1 : LIST [3 : 3] OF REAL;
v2 : LIST [3 : 3] OF REAL;
result : ifcvector;
  END_LOCAL;
    IF (NOT EXISTS(arg1) OR (arg1.dim = 2)) OR (NOT EXISTS(arg2) OR (arg2.dim = 2))
THEN
  RETURN(?);
ELSE
  BEGIN  
         v1 := ifcnormalise(arg1)\ifcdirection.directionratios;
         v2 := ifcnormalise(arg2)\ifcdirection.directionratios;
         res := ifcrepresentationitem() || ifcgeometricrepresentationitem() ||
                ifcdirection([(v1[2] * v2[3] - v1[3] * v2[2]),
                              (v1[3] * v2[1] - v1[1] * v2[3]),
                              (v1[1] * v2[2] - v1[2] * v2[1])]);
         mag := 0.0;
         REPEAT i := 1 TO 3;
           mag := mag + res.directionratios[i] * res.directionratios[i];
         END_REPEAT;
         IF (mag > 0.0)
         THEN
           result := ifcrepresentationitem() || ifcgeometricrepresentationitem() || ifcvector(res, SQRT(mag));
         ELSE
           result := ifcrepresentationitem() || ifcgeometricrepresentationitem() || ifcvector(arg1, 0.0);
         END_IF;
         RETURN(result);
  END;
END_IF;

  IF ('IFC4X2.IFCLINE' IN TYPEOF(curve))
THEN
  RETURN(curve\ifcline.pnt.dim);
END_IF;
    IF ('IFC4X2.IFCCONIC' IN TYPEOF(curve))
THEN
  RETURN(curve\ifcconic.position.dim);
END_IF;
    IF ('IFC4X2.IFCPOLYLINE' IN TYPEOF(curve))
THEN
  RETURN(curve\ifcpolyline.points[1].dim);
END_IF;
    IF ('IFC4X2.IFCTRIMMEDCURVE' IN TYPEOF(curve))
THEN
  RETURN(ifccurvedim(curve\ifctrimmedcurve.basiscurve));
END_IF;
    IF ('IFC4X2.IFCCOMPOSITECURVE' IN TYPEOF(curve))
THEN
  RETURN(curve\ifccompositecurve.segments[1].dim);
END_IF;
    IF ('IFC4X2.IFCBSPLINECURVE' IN TYPEOF(curve))
THEN
  RETURN(curve\ifcbsplinecurve.controlpointslist[1].dim);
END_IF;
    IF ('IFC4X2.IFCOFFSETCURVE2D' IN TYPEOF(curve))
THEN
  RETURN(2);
END_IF;
    IF ('IFC4X2.IFCOFFSETCURVE3D' IN TYPEOF(curve))
THEN
  RETURN(3);
END_IF;
    IF ('IFC4X2.IFCOFFSETCURVEBYDISTANCES' IN TYPEOF(curve))
THEN
  RETURN(3);
END_IF;
    IF ('IFC4X2.IFCCURVESEGMENT2D' IN TYPEOF(curve))
THEN
  RETURN(2);
END_IF;
    IF ('IFC4X2.IFCALIGNMENTCURVE' IN TYPEOF(curve))
THEN
  RETURN(3);
END_IF;
    IF ('IFC4X2.IFCPCURVE' IN TYPEOF(curve))
THEN
  RETURN(3);
END_IF;
    IF ('IFC4X2.IFCINDEXEDPOLYCURVE' IN TYPEOF(curve))
THEN
  RETURN(curve\ifcindexedpolycurve.points.dim);
END_IF;
    RETURN(?);

LOCAL
result : BOOLEAN := TRUE;
  END_LOCAL;
    REPEAT i := 0 TO b.upperindexoncontrolpoints;
  IF b.weights[i] <= 0.0
  THEN
    result := FALSE;
    RETURN(result);
  END_IF;
END_REPEAT;
    RETURN(result);

LOCAL
result : ifcdimensionalexponents := ifcdimensionalexponents(0, 0, 0, 0, 0, 0, 0);
  END_LOCAL;
    REPEAT i := LOINDEX(unitelements) TO HIINDEX(unitelements);
  result.lengthexponent := result.lengthexponent +
                           (unitelements[i].exponent * unitelements[i].unit.dimensions.lengthexponent);
  result.massexponent := result.massexponent +
                         (unitelements[i].exponent * unitelements[i].unit.dimensions.massexponent);
  result.timeexponent := result.timeexponent +
                         (unitelements[i].exponent * unitelements[i].unit.dimensions.timeexponent);
  result.electriccurrentexponent := result.electriccurrentexponent +
                                    (unitelements[i].exponent *
                                     unitelements[i].unit.dimensions.electriccurrentexponent);
  result.thermodynamictemperatureexponent := result.thermodynamictemperatureexponent +
                                             (unitelements[i].exponent *
                                              unitelements[i].unit.dimensions.thermodynamictemperatureexponent);
  result.amountofsubstanceexponent := result.amountofsubstanceexponent +
                                      (unitelements[i].exponent *
                                       unitelements[i].unit.dimensions.amountofsubstanceexponent);
  result.luminousintensityexponent := result.luminousintensityexponent +
                                      (unitelements[i].exponent *
                                       unitelements[i].unit.dimensions.luminousintensityexponent);
END_REPEAT;
    RETURN(result);

  CASE n OF
  metre : RETURN(ifcdimensionalexponents(1, 0, 0, 0, 0, 0, 0));
  square_metre : RETURN(ifcdimensionalexponents(2, 0, 0, 0, 0, 0, 0));
  cubic_metre : RETURN(ifcdimensionalexponents(3, 0, 0, 0, 0, 0, 0));
  gram : RETURN(ifcdimensionalexponents(0, 1, 0, 0, 0, 0, 0));
  second : RETURN(ifcdimensionalexponents(0, 0, 1, 0, 0, 0, 0));
  ampere : RETURN(ifcdimensionalexponents(0, 0, 0, 1, 0, 0, 0));
  kelvin : RETURN(ifcdimensionalexponents(0, 0, 0, 0, 1, 0, 0));
  mole : RETURN(ifcdimensionalexponents(0, 0, 0, 0, 0, 1, 0));
  candela : RETURN(ifcdimensionalexponents(0, 0, 0, 0, 0, 0, 1));
  radian : RETURN(ifcdimensionalexponents(0, 0, 0, 0, 0, 0, 0));
  steradian : RETURN(ifcdimensionalexponents(0, 0, 0, 0, 0, 0, 0));
  hertz : RETURN(ifcdimensionalexponents(0, 0, -1, 0, 0, 0, 0));
  newton : RETURN(ifcdimensionalexponents(1, 1, -2, 0, 0, 0, 0));
  pascal : RETURN(ifcdimensionalexponents(-1, 1, -2, 0, 0, 0, 0));
  joule : RETURN(ifcdimensionalexponents(2, 1, -2, 0, 0, 0, 0));
  watt : RETURN(ifcdimensionalexponents(2, 1, -3, 0, 0, 0, 0));
  coulomb : RETURN(ifcdimensionalexponents(0, 0, 1, 1, 0, 0, 0));
  volt : RETURN(ifcdimensionalexponents(2, 1, -3, -1, 0, 0, 0));
  farad : RETURN(ifcdimensionalexponents(-2, -1, 4, 2, 0, 0, 0));
  ohm : RETURN(ifcdimensionalexponents(2, 1, -3, -2, 0, 0, 0));
  siemens : RETURN(ifcdimensionalexponents(-2, -1, 3, 2, 0, 0, 0));
  weber : RETURN(ifcdimensionalexponents(2, 1, -2, -1, 0, 0, 0));
  tesla : RETURN(ifcdimensionalexponents(0, 1, -2, -1, 0, 0, 0));
  henry : RETURN(ifcdimensionalexponents(2, 1, -2, -2, 0, 0, 0));
  degree_celsius : RETURN(ifcdimensionalexponents(0, 0, 0, 0, 1, 0, 0));
  lumen : RETURN(ifcdimensionalexponents(0, 0, 0, 0, 0, 0, 1));
  lux : RETURN(ifcdimensionalexponents(-2, 0, 0, 0, 0, 0, 1));
  becquerel : RETURN(ifcdimensionalexponents(0, 0, -1, 0, 0, 0, 0));
  gray : RETURN(ifcdimensionalexponents(2, 0, -2, 0, 0, 0, 0));
  sievert : RETURN(ifcdimensionalexponents(2, 0, -2, 0, 0, 0, 0));
  OTHERWISE: RETURN(ifcdimensionalexponents(0, 0, 0, 0, 0, 0, 0));
END_CASE;

LOCAL
scalar : REAL;
vec1 : ifcdirection;
vec2 : ifcdirection;
ndim : INTEGER;
  END_LOCAL;
    IF NOT EXISTS(arg1) OR NOT EXISTS(arg2)
THEN
  scalar := ?;
ELSE
  IF (arg1.dim <> arg2.dim)
  THEN
    scalar := ?;
  ELSE
    BEGIN  
           vec1 := ifcnormalise(arg1);
           vec2 := ifcnormalise(arg2);
           ndim := arg1.dim;
           scalar := 0.0;
           REPEAT i := 1 TO ndim;
             scalar := scalar + vec1.directionratios[i] * vec2.directionratios[i];
           END_REPEAT;
    END;
  END_IF;
END_IF;
    RETURN(scalar);

LOCAL
xaxis : ifcdirection;
v : ifcdirection;
z : ifcdirection;
xvec : ifcvector;
  END_LOCAL;
    IF (NOT EXISTS(zaxis))
THEN
  RETURN(?);
ELSE
  z := ifcnormalise(zaxis);
  IF NOT EXISTS(arg)
  THEN
    IF (z.directionratios <> [1.0,0.0,0.0])
    THEN
      v := ifcrepresentationitem() || ifcgeometricrepresentationitem() || ifcdirection([1.0,0.0,0.0]);
    ELSE
      v := ifcrepresentationitem() || ifcgeometricrepresentationitem() || ifcdirection([0.0,1.0,0.0]);
    END_IF;
  ELSE
    IF (arg.dim <> 3)
    THEN
      RETURN(?);
    END_IF;
    IF ((ifccrossproduct(arg, z).magnitude) = 0.0)
    THEN
      RETURN(?);
    ELSE
      v := ifcnormalise(arg);
    END_IF;
  END_IF;
  xvec := ifcscalartimesvector(ifcdotproduct(v, z), z);
  xaxis := ifcvectordifference(v, xvec).orientation;
  xaxis := ifcnormalise(xaxis);
END_IF;
    RETURN(xaxis);

LOCAL
surfs : SET [0 : 2] OF ifcsurface;
n : INTEGER;
  END_LOCAL;
    surfs := [];
    IF 'IFC4X2.IFCPCURVE' IN TYPEOF(c)
THEN
  surfs := [c\ifcpcurve.basissurface];
ELSE
  IF 'IFC4X2.IFCSURFACECURVE' IN TYPEOF(c)
  THEN
    n := SIZEOF(c\ifcsurfacecurve.associatedgeometry);
    REPEAT i := 1 TO n;
      surfs := surfs + ifcassociatedsurface(c\ifcsurfacecurve.associatedgeometry[i]);
    END_REPEAT;
  END_IF;
END_IF;
    IF 'IFC4X2.IFCCOMPOSITECURVEONSURFACE' IN TYPEOF(c)
THEN
  n := SIZEOF(c\ifccompositecurve.segments);
  surfs := ifcgetbasissurface(c\ifccompositecurve.segments[1].parentcurve);
  IF n > 1
  THEN
    REPEAT i := 2 TO n;
      surfs := surfs * ifcgetbasissurface(c\ifccompositecurve.segments[1].parentcurve);
    END_REPEAT;
  END_IF;
END_IF;
    RETURN(surfs);

LOCAL
n : INTEGER;
res : ARRAY [low : u] OF GENERIC:T;
  END_LOCAL;
    n := SIZEOF(lis);
    IF (n <> (u - low + 1))
THEN
  RETURN(?);
ELSE
  res := [lis[1]:n];
  REPEAT i := 2 TO n;
    res[low + i - 1] := lis[i];
  END_REPEAT;
  RETURN(res);
END_IF;

LOCAL
n : INTEGER;
p : LOGICAL := TRUE;
  END_LOCAL;
    n := SIZEOF(aloop.edgelist);
    REPEAT i := 2 TO n;
  p := p AND (aloop.edgelist[i - 1].edgeend :=: aloop.edgelist[i].edgestart);
END_REPEAT;
    RETURN(p);

LOCAL
res : ARRAY [low1 : u1] OF ARRAY [low2 : u2] OF GENERIC:T;
  END_LOCAL;
    (*Check input dimensions for consistency*)

    IF (u1 - low1 + 1) <> SIZEOF(lis)
THEN
  RETURN(?);
END_IF;
    IF (u2 - low2 + 1) <> SIZEOF(lis[1])
THEN
  RETURN(?);
END_IF;
    (*Initialise Res with values from Lis[1]*)

    res := [ifclisttoarray(lis[1], low2, u2):(u1 - low1 + 1)];
    REPEAT i := 2 TO HIINDEX(lis);
  IF (u2 - low2 + 1) <> SIZEOF(lis[i])
  THEN
    RETURN(?);
  END_IF;
  res[low1 + i - 1] := ifclisttoarray(lis[i], low2, u2);
END_REPEAT;
    RETURN(res);

LOCAL
max : ifclengthmeasure := layerset.materiallayers[1].layerthickness;
  END_LOCAL;
    IF SIZEOF(layerset.materiallayers) > 1
THEN
  REPEAT i := 2 TO HIINDEX(layerset.materiallayers);
    max := max + layerset.materiallayers[i].layerthickness;
  END_REPEAT;
END_IF;
    RETURN(max);

LOCAL
ndim : INTEGER;
v : ifcdirection := ifcrepresentationitem() || ifcgeometricrepresentationitem() || ifcdirection([1.,0.]);
vec : ifcvector := ifcrepresentationitem() || ifcgeometricrepresentationitem() ||
                   ifcvector(ifcrepresentationitem() || ifcgeometricrepresentationitem() || ifcdirection([1.,0.]),
                             1.);
mag : REAL;
result : ifcvectorordirection := v;
  END_LOCAL;
    IF NOT EXISTS(arg)
THEN
  RETURN(?);
ELSE
  IF 'IFC4X2.IFCVECTOR' IN TYPEOF(arg)
  THEN
    BEGIN  
           ndim := arg\ifcvector.dim;
           v.directionratios := arg\ifcvector.orientation.directionratios;
           vec.magnitude := arg\ifcvector.magnitude;
           vec.orientation := v;
           IF arg\ifcvector.magnitude = 0.0
           THEN
             RETURN(?);
           ELSE
             vec.magnitude := 1.0;
           END_IF;
    END;
  ELSE
    BEGIN  
           ndim := arg\ifcdirection.dim;
           v.directionratios := arg\ifcdirection.directionratios;
    END;
  END_IF;
  mag := 0.0;
  REPEAT i := 1 TO ndim;
    mag := mag + v.directionratios[i] * v.directionratios[i];
  END_REPEAT;
  IF mag > 0.0
  THEN
    mag := SQRT(mag);
    REPEAT i := 1 TO ndim;
      v.directionratios[i] := v.directionratios[i] / mag;
    END_REPEAT;
    IF 'IFC4X2.IFCVECTOR' IN TYPEOF(arg)
    THEN
      vec.orientation := v;
      result := vec;
    ELSE
      result := v;
    END_IF;
  ELSE
    RETURN(?);
  END_IF;
END_IF;
    RETURN(result);

LOCAL
result : ifcdirection;
  END_LOCAL;
    IF NOT EXISTS(vec) OR (vec.dim <> 2)
THEN
  RETURN(?);
ELSE
  result := ifcrepresentationitem() || ifcgeometricrepresentationitem() ||
            ifcdirection([-vec.directionratios[2],vec.directionratios[1]]);
  RETURN(result);
END_IF;

LOCAL
n : INTEGER := 0;
p : LOGICAL := UNKNOWN;
  END_LOCAL;
    n := SIZEOF(apath.edgelist);
    REPEAT i := 2 TO n;
  p := p AND (apath.edgelist[i - 1].edgeend :=: apath.edgelist[i].edgestart);
END_REPEAT;
    RETURN(p);

  IF ('IFC4X2.IFCCARTESIANPOINTLIST2D' IN TYPEOF(pointlist))
THEN
  RETURN(2);
END_IF;
    IF ('IFC4X2.IFCCARTESIANPOINTLIST3D' IN TYPEOF(pointlist))
THEN
  RETURN(3);
END_IF;
    RETURN(?);

RETURN(ifcsamedirection(ap1.p[1], ap2.p[1], epsilon) AND
                        ifcsamedirection(ap1.p[2], ap2.p[2], epsilon) AND
                        ifcsamecartesianpoint(ap1.location, ap1.location, epsilon));

LOCAL
cp1x : REAL := cp1.coordinates[1];
cp1y : REAL := cp1.coordinates[2];
cp1z : REAL := 0;
cp2x : REAL := cp2.coordinates[1];
cp2y : REAL := cp2.coordinates[2];
cp2z : REAL := 0;
  END_LOCAL;
    IF (SIZEOF(cp1.coordinates) > 2)
THEN
  cp1z := cp1.coordinates[3];
END_IF;
    IF (SIZEOF(cp2.coordinates) > 2)
THEN
  cp2z := cp2.coordinates[3];
END_IF;
    RETURN(ifcsamevalue(cp1x, cp2x, epsilon) AND ifcsamevalue(cp1y, cp2y, epsilon) AND
           ifcsamevalue(cp1z, cp2z, epsilon));

LOCAL
dir1x : REAL := dir1.directionratios[1];
dir1y : REAL := dir1.directionratios[2];
dir1z : REAL := 0;
dir2x : REAL := dir2.directionratios[1];
dir2y : REAL := dir2.directionratios[2];
dir2z : REAL := 0;
  END_LOCAL;
    IF (SIZEOF(dir1.directionratios) > 2)
THEN
  dir1z := dir1.directionratios[3];
END_IF;
    IF (SIZEOF(dir2.directionratios) > 2)
THEN
  dir2z := dir2.directionratios[3];
END_IF;
    RETURN(ifcsamevalue(dir1x, dir2x, epsilon) AND ifcsamevalue(dir1y, dir2y, epsilon) AND
           ifcsamevalue(dir1z, dir2z, epsilon));

LOCAL
valideps1 : REAL;
valideps2 : REAL;
defaulteps : REAL := 0.000001;
derivationofeps : REAL := 1.001;
uppereps : REAL := 1.0;
  END_LOCAL;
    valideps1 := NVL(epsilon1, defaulteps);
    valideps2 := NVL(epsilon2, defaulteps);
    RETURN((0.0 < valideps1) AND (valideps1 <= (derivationofeps * valideps2)) AND
           (valideps2 <= (derivationofeps * valideps1)) AND (valideps2 < uppereps));

LOCAL
valideps : REAL;
defaulteps : REAL := 0.000001;
  END_LOCAL;
    valideps := NVL(epsilon, defaulteps);
    RETURN((value1 + valideps > value2) AND (value1 < value2 + valideps));

LOCAL
v : ifcdirection;
mag : REAL;
result : ifcvector;
  END_LOCAL;
    IF NOT EXISTS(scalar) OR NOT EXISTS(vec)
THEN
  RETURN(?);
ELSE
  IF 'IFC4X2.IFCVECTOR' IN TYPEOF(vec)
  THEN
    v := vec\ifcvector.orientation;
    mag := scalar * vec\ifcvector.magnitude;
  ELSE
    v := vec;
    mag := scalar;
  END_IF;
  IF (mag < 0.0)
  THEN
    REPEAT i := 1 TO SIZEOF(v.directionratios);
      v.directionratios[i] := -v.directionratios[i];
    END_REPEAT;
    mag := -mag;
  END_IF;
  result := ifcrepresentationitem() || ifcgeometricrepresentationitem() || ifcvector(ifcnormalise(v), mag);
END_IF;
    RETURN(result);

LOCAL
yaxis : ifcvector;
v : ifcdirection;
temp : ifcvector;
  END_LOCAL;
    IF NOT EXISTS(arg)
THEN
  v := ifcrepresentationitem() || ifcgeometricrepresentationitem() || ifcdirection([0.0,1.0,0.0]);
ELSE
  v := arg;
END_IF;
    temp := ifcscalartimesvector(ifcdotproduct(v, zaxis), zaxis);
    yaxis := ifcvectordifference(v, temp);
    temp := ifcscalartimesvector(ifcdotproduct(v, xaxis), xaxis);
    yaxis := ifcvectordifference(yaxis, temp);
    yaxis := ifcnormalise(yaxis);
    RETURN(yaxis.orientation);

