LOCAL
result : BOOLEAN := TRUE;
weights : ARRAY [0 : b\ifcbsplinesurface.uupper] OF ARRAY [0 : b\ifcbsplinesurface.vupper] OF REAL := b.weights;
  END_LOCAL;
    REPEAT i := 0 TO b\ifcbsplinesurface.uupper;
  REPEAT j := 0 TO b\ifcbsplinesurface.vupper;
    IF (weights[i][j] <= 0.0)
    THEN
      result := FALSE;
      RETURN(result);
    END_IF;
  END_REPEAT;
END_REPEAT;
    RETURN(result);

