LOCAL
definition : ifcpropertysetdefinitionselect;
definitionset : ifcpropertysetdefinitionset;
properties : SET OF ifcpropertysetdefinition := [];
result : LOGICAL;
  END_LOCAL;
    IF SIZEOF(relations) = 0
THEN
  RETURN(TRUE);
END_IF;
    REPEAT i := 1 TO HIINDEX(relations);
  definition := relations[i].relatingpropertydefinition;
  IF 'IFC4X2.IFCPROPERTYSETDEFINITION' IN TYPEOF(definition)
  THEN
    properties := properties + definition;
  ELSE
    IF 'IFC4X2.IFCPROPERTYSETDEFINITIONSET' IN TYPEOF(definition)
    THEN
      BEGIN  
             definitionset := definition;
             REPEAT j := 1 TO HIINDEX(definitionset);
               properties := properties + definitionset[j];
             END_REPEAT;
      END;
    END_IF;
  END_IF;
END_REPEAT;
    result := ifcuniquepropertysetnames(properties);
    RETURN(result);

LOCAL
names : SET OF ifcidentifier := [];
  END_LOCAL;
    REPEAT i := 1 TO HIINDEX(properties);
  names := names + properties[i].name;
END_REPEAT;
    RETURN(SIZEOF(names) = SIZEOF(properties));

LOCAL
names : SET OF ifclabel := [];
unnamed : INTEGER := 0;
  END_LOCAL;
    REPEAT i := 1 TO HIINDEX(properties);
  IF 'IFC4X2.IFCPROPERTYSET' IN TYPEOF(properties[i])
  THEN
    names := names + properties[i]\ifcroot.name;
  ELSE
    unnamed := unnamed + 1;
  END_IF;
END_REPEAT;
    RETURN(SIZEOF(names) + unnamed = SIZEOF(properties));

LOCAL
names : SET OF ifclabel := [];
  END_LOCAL;
    REPEAT i := 1 TO HIINDEX(properties);
  names := names + properties[i].name;
END_REPEAT;
    RETURN(SIZEOF(names) = SIZEOF(properties));

LOCAL
names : SET OF ifclabel := [];
  END_LOCAL;
    REPEAT i := 1 TO HIINDEX(properties);
  names := names + properties[i].name;
END_REPEAT;
    RETURN(SIZEOF(names) = SIZEOF(properties));

LOCAL
result : ifcvector;
res : ifcdirection;
vec1 : ifcdirection;
vec2 : ifcdirection;
mag : REAL;
mag1 : REAL;
mag2 : REAL;
ndim : INTEGER;
  END_LOCAL;
    IF ((NOT EXISTS(arg1)) OR (NOT EXISTS(arg2))) OR (arg1.dim <> arg2.dim)
THEN
  RETURN(?);
ELSE
  BEGIN  
         IF 'IFC4X2.IFCVECTOR' IN TYPEOF(arg1)
         THEN
           mag1 := arg1\ifcvector.magnitude;
           vec1 := arg1\ifcvector.orientation;
         ELSE
           mag1 := 1.0;
           vec1 := arg1;
         END_IF;
         IF 'IFC4X2.IFCVECTOR' IN TYPEOF(arg2)
         THEN
           mag2 := arg2\ifcvector.magnitude;
           vec2 := arg2\ifcvector.orientation;
         ELSE
           mag2 := 1.0;
           vec2 := arg2;
         END_IF;
         vec1 := ifcnormalise(vec1);
         vec2 := ifcnormalise(vec2);
         ndim := SIZEOF(vec1.directionratios);
         mag := 0.0;
         res := ifcrepresentationitem() || ifcgeometricrepresentationitem() || ifcdirection([0.0:ndim]);
         REPEAT i := 1 TO ndim;
           res.directionratios[i] := mag1 * vec1.directionratios[i] - mag2 * vec2.directionratios[i];
           mag := mag + (res.directionratios[i] * res.directionratios[i]);
         END_REPEAT;
         IF (mag > 0.0)
         THEN
           result := ifcrepresentationitem() || ifcgeometricrepresentationitem() || ifcvector(res, SQRT(mag));
         ELSE
           result := ifcrepresentationitem() || ifcgeometricrepresentationitem() || ifcvector(vec1, 0.0);
         END_IF;
  END;
END_IF;
    RETURN(result);

LOCAL
result : ifcvector;
res : ifcdirection;
vec1 : ifcdirection;
vec2 : ifcdirection;
mag : REAL;
mag1 : REAL;
mag2 : REAL;
ndim : INTEGER;
  END_LOCAL;
    IF ((NOT EXISTS(arg1)) OR (NOT EXISTS(arg2))) OR (arg1.dim <> arg2.dim)
THEN
  RETURN(?);
ELSE
  BEGIN  
         IF 'IFC4X2.IFCVECTOR' IN TYPEOF(arg1)
         THEN
           mag1 := arg1\ifcvector.magnitude;
           vec1 := arg1\ifcvector.orientation;
         ELSE
           mag1 := 1.0;
           vec1 := arg1;
         END_IF;
         IF 'IFC4X2.IFCVECTOR' IN TYPEOF(arg2)
         THEN
           mag2 := arg2\ifcvector.magnitude;
           vec2 := arg2\ifcvector.orientation;
         ELSE
           mag2 := 1.0;
           vec2 := arg2;
         END_IF;
         vec1 := ifcnormalise(vec1);
         vec2 := ifcnormalise(vec2);
         ndim := SIZEOF(vec1.directionratios);
         mag := 0.0;
         res := ifcrepresentationitem() || ifcgeometricrepresentationitem() || ifcdirection([0.0:ndim]);
         REPEAT i := 1 TO ndim;
           res.directionratios[i] := mag1 * vec1.directionratios[i] + mag2 * vec2.directionratios[i];
           mag := mag + (res.directionratios[i] * res.directionratios[i]);
         END_REPEAT;
         IF (mag > 0.0)
         THEN
           result := ifcrepresentationitem() || ifcgeometricrepresentationitem() || ifcvector(res, SQRT(mag));
         ELSE
           result := ifcrepresentationitem() || ifcgeometricrepresentationitem() || ifcvector(vec1, 0.0);
         END_IF;
  END;
END_IF;
    RETURN(result);

rule.algorithm:
  LOCAL
isdifferent : LOGICAL := FALSE;
  END_LOCAL;
IF (SIZEOF(ifcgeometricrepresentationcontext) > 1)
THEN
  REPEAT i := 2 TO HIINDEX(ifcgeometricrepresentationcontext);
    IF (ifcgeometricrepresentationcontext[1].worldcoordinatesystem :<>:
        ifcgeometricrepresentationcontext[i].worldcoordinatesystem)
    THEN
      isdifferent := (NOT (ifcsamevalidprecision(ifcgeometricrepresentationcontext[1].precision, ifcgeometricrepresentationcontext[i].precision))) OR
                     (NOT (ifcsameaxis2placement(ifcgeometricrepresentationcontext[1].worldcoordinatesystem, ifcgeometricrepresentationcontext[i].worldcoordinatesystem, ifcgeometricrepresentationcontext[1].precision)));
      IF (isdifferent = TRUE)
      THEN
        ESCAPE;
      END_IF;
    END_IF;
  END_REPEAT;
END_IF;
